import React, { useState } from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, Typography, TextField, CircularProgress, Snackbar } from '@material-ui/core';
import MuiAlert from '@material-ui/lab/Alert';
import { makeStyles } from '@material-ui/core/styles';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  // ... (rest of the styles remain unchanged)
}));

function App() {
  const classes = useStyles();

  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [downloaded, setDownloaded] = useState(false);
  const [error, setError] = useState(""); // New state for error

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError(""); // Reset error when a file is uploaded
  };

  const handleGenerate = async (event) => {
    event.preventDefault();

    if (!file) { // Check if file is uploaded
      setError("Please upload a file for data generation");
      return;
    }

    setLoading(true);

    // ... (rest of the handleGenerate function remains unchanged)
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5" className={classes.title}>
          JLL's Data Doppelg√§nger
        </Typography>
        {loading ? <CircularProgress /> : (
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="file"
              label="Upload File"
              type="file"
              autoComplete="off"
              autoFocus
              onChange={handleFileChange}
              error={!!error} // show error if there's an error message
              helperText={error} // display error message
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick={handleGenerate}
              disabled={!file}
            >
              Generate Data
            </Button>
          </form>
        )}

        <Snackbar open={downloaded} autoHideDuration={6000} onClose={() => {
          setDownloaded(false);
          setFile(null); // Refresh the file input upon successful download
          document.getElementById("file").value = ""; // Reset the file input
        }}>
          <Alert onClose={() => setDownloaded(false)} severity="success">
            File downloaded successfully!
          </Alert>
        </Snackbar>
      </div>
    </Container>
  );
};

export default App;
