import React, { useState } from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, Typography, TextField, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  form: {
    width: '100%', 
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function App() {
  const classes = useStyles();

  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    console.log(event.target.files[0])
    setFile(event.target.files[0]);
  };

  // Add state for dummy data
const [dummyData, setDummyData] = useState("");

const handleGenerate = async (event) => {
  event.preventDefault(); // Prevent form from submitting

  const formData = new FormData();
  formData.append('sampleFile', file);

  try {
      const response = await axios.post('http://localhost:4028/generate', formData, {
          responseType: 'blob'  // tell axios to treat the response as a Blob
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'out.xlsx'); // Name the download file as 'out.xlsx'
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      console.log('Data fetched and file downloaded successfully');
  } catch (error) {
      console.error('Error fetching data:', error);
  }
};


// In the render (return) part of the component:
return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
           Data Droppleganger
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="file"
            label="Upload File"
            type="file"
            autoComplete="off"
            autoFocus
            onChange={handleFileChange}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={handleGenerate}
            disabled={!file}
          >
            Generate Data
          </Button>
        </form>
        {dummyData && (
          <div style={{ marginTop: '20px' }}>
            <Typography variant="h6">Generated Dummy Data:</Typography>
            <textarea
              style={{ width: '100%', height: '200px', marginTop: '10px' }}
              readOnly
              value={dummyData}
            />
          </div>
        )}
      </div>
    </Container>
);
        };

export default App;
