// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import { Button, TextField, Typography, CssBaseline, Container } from '@material-ui/core';

function App() {
  const [file, setFile] = useState(null);
  const [anonymize, setAnonymize] = useState(false);
  const [randomize, setRandomize] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleGenerate = async () => {
    const formData = new FormData();
    formData.append('sampleFile', file);
    formData.append('anonymize', anonymize);
    formData.append('randomize', randomize);
    try {
      const response = await axios.post('http://localhost:3001/generate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        responseType: 'blob',
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-data.csv');
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.error('Error generating data:', error);
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div style={{ marginTop: '8px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Typography component="h1" variant="h5">
          TestDataGenerator
        </Typography>
        <input type="file" onChange={handleFileChange} style={{ margin: '20px 0' }} />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          onClick={handleGenerate}
          disabled={!file}
        >
          Generate Data
        </Button>
      </div>
    </Container>
  );
}

export default App;
