import React, { useState } from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, Typography, TextField, Grid, CircularProgress, Snackbar } from '@material-ui/core';
import MuiAlert from '@material-ui/lab/Alert';
import { makeStyles } from '@material-ui/core/styles';

// For the Snackbar alert
function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  form: {
    width: '100%', 
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function App() {
  const classes = useStyles();

  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [downloaded, setDownloaded] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleGenerate = async (event) => {
    event.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append('sampleFile', file);

    try {
      const response = await axios.post('http://localhost:4028/generate', formData, {
          responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'out.xlsx');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      setDownloaded(true);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
        Data Doppelg√§nger
        </Typography>
        {loading ? <CircularProgress /> : (
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="file"
              label="Upload File"
              type="file"
              autoComplete="off"
              autoFocus
              onChange={handleFileChange}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick={handleGenerate}
              disabled={!file}
            >
              Generate Data
            </Button>
          </form>
        )}

        <Snackbar open={downloaded} autoHideDuration={6000} onClose={() => setDownloaded(false)}>
          <Alert onClose={() => setDownloaded(false)} severity="success">
            File downloaded successfully!
          </Alert>
        </Snackbar>
      </div>
    </Container>
  );
};

export default App;
