import React, { useState } from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, Typography, TextField, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  form: {
    width: '100%', 
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function App() {
  const classes = useStyles();

  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    console.log(event.target.files[0])
    setFile(event.target.files[0]);
  };

  const handleGenerate = async (event) => {
    event.preventDefault(); // Prevent form from submitting

    const formData = new FormData();
    formData.append('sampleFile', file);

    try {
        // Make the axios call and expect a file in response
        const response = await axios.post('http://localhost:4028/generate', formData, {
            responseType: 'blob', // Indicate that we're expecting a Blob (binary data)
        });

        // Create a URL for the blob
        const url = window.URL.createObjectURL(new Blob([response.data]));

        // Create a temporary link element
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'dummy_data.csv'); // Suggest a filename for download

        // Append the link to the document, trigger a click to start download, and remove the link
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        console.log('Data downloaded successfully');
    } catch (error) {
        console.error('Error generating data:', error);
    }
};


  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5">
           Data Droppleganger
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="file"
            label="Upload File"
            type="file"
            autoComplete="off"
            autoFocus
            onChange={handleFileChange}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={handleGenerate}
            disabled={!file}
          >
            Generate Data
          </Button>
        </form>
      </div>
    </Container>
  );
}

export default App;
