import React, { useState } from 'react';
import axios from 'axios';
import { Button, Container, CssBaseline, Typography, TextField, CircularProgress, Snackbar } from '@material-ui/core';
import MuiAlert from '@material-ui/lab/Alert';
import { makeStyles } from '@material-ui/core/styles';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  // ... (rest of the styles remain unchanged)
}));

function App() {
  const classes = useStyles();

  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [downloaded, setDownloaded] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError("");
  };

  const handleGenerate = async (event) => {
    event.preventDefault();

    if (!file) {
      setError("Please upload a file for data generation");
      return;
    }

    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setSubmitted(true);
    }, 3000);
  };

  const handleSubmitToOpenAI = async () => {
    setLoading(true);
    try {
        const response = await axios.get('http://localhost:4028/generateExcel', {
            responseType: 'blob',
        });

        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'users.xlsx');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        setLoading(false);
        setDownloaded(true);
    } catch (error) {
        console.error('Error downloading Excel:', error);
        setLoading(false);
        setError('Error downloading the file. Please try again.');
    }
  }

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Typography component="h1" variant="h5" className={classes.title}>
          JLL's Data Doppelg√§nger
        </Typography>
        {loading ? <CircularProgress /> : (
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="file"
              label="Upload File"
              type="file"
              autoComplete="off"
              autoFocus
              onChange={handleFileChange}
              error={!!error}
              helperText={error}
            />
            {!submitted ? (
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                className={classes.submit}
                onClick={handleGenerate}
                disabled={!file}
              >
                Generate Data
              </Button>
            ) : (
              <Button
                fullWidth
                variant="contained"
                color="secondary"
                className={classes.submit}
                onClick={handleSubmitToOpenAI}
              >
                Submit to OpenAI
              </Button>
            )}
          </form>
        )}

        <Snackbar open={downloaded} autoHideDuration={6000} onClose={() => {
          setDownloaded(false);
          setFile(null);
          document.getElementById("file").value = "";
        }}>
          <Alert onClose={() => setDownloaded(false)} severity="success">
            File downloaded successfully!
          </Alert>
        </Snackbar>
      </div>
    </Container>
  );
};

export default App;
